function InteractiveDetailsDemo() {
  const [isOpen, setIsOpen] = useState(false);
  const [maxWidth, setMaxWidth] = useState('75ch');
  const [spacing, setSpacing] = useState('m');

  return (
    <GoabBlock gap="l" direction="column">
      <GoabBlock gap="m" direction="row">
        <GoabButton 
          type="tertiary" 
          onClick={() => setIsOpen(!isOpen)}
        >
          {isOpen ? 'Close' : 'Open'} Details
        </GoabButton>

        <GoabSelect 
          value={maxWidth} 
          onChange={(name, value) => setMaxWidth(value)}
          placeholder="Max Width"
        >
          <option value="75ch">75ch (default)</option>
          <option value="400px">400px</option>
          <option value="600px">600px</option>
          <option value="100%">100%</option>
        </GoabSelect>
      </GoabBlock>

      <GoabDetails 
        heading="Interactive Details Example"
        open={isOpen}
        maxWidth={maxWidth}
        mb={spacing}
        onToggle={setIsOpen}
      >
        <p>This content's width is controlled by the maxWidth prop: <strong>{maxWidth}</strong></p>
        <p>Use the controls above to test different configurations and see how the component responds to prop changes.</p>
        <ul>
          <li>Toggle the open state with the button</li>
          <li>Adjust maxWidth to see content reflow</li>
          <li>Notice how the component maintains readability</li>
        </ul>
      </GoabDetails>
    </GoabBlock>
  );
}

