function FAQSection() {
  const [searchTerm, setSearchTerm] = useState('');
  const [openAll, setOpenAll] = useState(false);

  const faqs = [
    {
      id: 1,
      question: "Can I change my business name after registration?",
      answer: "Yes, you can change your business name, but the process depends on your business structure. For sole proprietorships, you'll need to register a new business name and close the old registration."
    },
    {
      id: 2,
      question: "Do I need to register if I'm working from home?",
      answer: "Generally yes, if you're operating a business for profit, you need to register regardless of location. However, there are exceptions for occasional sales of personal items or hobby activities."
    },
    {
      id: 3,
      question: "What's the difference between registration and incorporation?",
      answer: "Business registration is simpler and less expensive but offers no liability protection. Incorporation creates a separate legal entity with limited liability protection but has more complex requirements."
    }
  ];

  const filteredFAQs = faqs.filter(faq => 
    faq.question.toLowerCase().includes(searchTerm.toLowerCase()) ||
    faq.answer.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <GoabBlock gap="l" direction="column">
      <GoabBlock gap="m" direction="row" alignment="start">
        <GoabInput 
          placeholder="Search FAQs..."
          value={searchTerm}
          onChange={(name, value) => setSearchTerm(value)}
        />
        <GoabButton 
          type="tertiary"
          onClick={() => setOpenAll(!openAll)}
        >
          {openAll ? 'Collapse All' : 'Expand All'}
        </GoabButton>
      </GoabBlock>

      <GoabText element="h2">Frequently Asked Questions</GoabText>

      {filteredFAQs.length === 0 ? (
        <GoabCallout type="information">
          No FAQs match your search term. Try a different search or clear the filter.
        </GoabCallout>
      ) : (
        filteredFAQs.map(faq => (
          <GoabDetails 
            key={faq.id}
            heading={faq.question}
            open={openAll}
            mb="s"
          >
            <p>{faq.answer}</p>
          </GoabDetails>
        ))
      )}
    </GoabBlock>
  );
}

stateManagement.code:
function AdvancedStateManagement() {
  const [detailsStates, setDetailsStates] = useState({
    personal: false,
    employment: false,
    documents: false
  });
  const [eventLog, setEventLog] = useState([]);
  const [isControlled, setIsControlled] = useState(true);

  const handleToggle = (section, isOpen) => {
    if (isControlled) {
      setDetailsStates(prev => ({ ...prev, [section]: isOpen }));
    }

    setEventLog(prev => [...prev, {
      timestamp: new Date().toLocaleTimeString(),
      section,
      action: isOpen ? 'expanded' : 'collapsed'
    }]);
  };

  const openAll = () => {
    setDetailsStates({ personal: true, employment: true, documents: true });
  };

  const closeAll = () => {
    setDetailsStates({ personal: false, employment: false, documents: false });
  };

  return (
    <GoabBlock gap="l" direction="column">
      <GoabBlock gap="m" direction="row">
        <GoabButton type="tertiary" onClick={openAll}>
          Open All Sections
        </GoabButton>
        <GoabButton type="tertiary" onClick={closeAll}>
          Close All Sections
        </GoabButton>
        <GoabButton 
          type="tertiary" 
          onClick={() => setIsControlled(!isControlled)}
        >
          {isControlled ? 'Uncontrolled' : 'Controlled'} Mode
        </GoabButton>
      </GoabBlock>

      <GoabDetails 
        heading="Personal Information Help"
        open={isControlled ? detailsStates.personal : undefined}
        onToggle={(isOpen) => handleToggle('personal', isOpen)}
        testId="personal-help-details"
      >
        <p>Provide your legal name, address, and contact information as they appear on official documents.</p>
        <ul>
          <li>Use your full legal name (including middle names)</li>
          <li>Provide current mailing address</li>
          <li>Include area code with phone numbers</li>
        </ul>
      </GoabDetails>

      <GoabDetails 
        heading="Employment Information Help"
        open={isControlled ? detailsStates.employment : undefined}
        onToggle={(isOpen) => handleToggle('employment', isOpen)}
        testId="employment-help-details"
      >
        <p>Include all current employment and income sources, even part-time or seasonal work.</p>
        <ul>
          <li>List primary employer first</li>
          <li>Include gross monthly income</li>
          <li>Add self-employment or contract work</li>
        </ul>
      </GoabDetails>

      <GoabDetails 
        heading="Required Documents Help"
        open={isControlled ? detailsStates.documents : undefined}
        onToggle={(isOpen) => handleToggle('documents', isOpen)}
        testId="documents-help-details"
      >
        <p>Upload clear, readable copies of all required supporting documents.</p>
        <ul>
          <li>Government-issued photo ID</li>
          <li>Proof of income (pay stubs, tax returns)</li>
          <li>Proof of address (utility bill, lease)</li>
        </ul>
      </GoabDetails>

      {eventLog.length > 0 && (
        <GoabContainer type="info">
          <GoabText element="h4">Event Log:</GoabText>
          <ul>
            {eventLog.slice(-5).map((event, index) => (
              <li key={index}>
                {event.timestamp}: {event.section} section {event.action}
              </li>
            ))}
          </ul>
        </GoabContainer>
      )}
    </GoabBlock>
  );
}

