// ✅ Proper controlled component pattern
const [isHelpOpen, setIsHelpOpen] = useState(false);

<GoabDetails 
  heading="Additional Information"
  open={isHelpOpen}
  onToggle={setIsHelpOpen}  // Updates parent state
>
  <p>Help content here...</p>
</GoabDetails>

// ✅ Master control for multiple details
const [showAllHelp, setShowAllHelp] = useState(false);

<GoabButton onClick={() => setShowAllHelp(!showAllHelp)}>
  {showAllHelp ? 'Hide All Help' : 'Show All Help'}
</GoabButton>

{helpSections.map(section => (
  <GoabDetails 
    key={section.id}
    heading={section.heading}
    open={showAllHelp}
    onToggle={(isOpen) => {
      // Individual control still works
      if (!isOpen) setShowAllHelp(false);
    }}
  >
    {section.content}
  </GoabDetails>
))}

