import React, { useState } from 'react';
import { GoabDrawer, GoabFormItem, GoabInput, GoabButton, GoabBlock } from '@abgov/react-components';

interface UserProfile {
  firstName: string;
  lastName: string;
  email: string;
  department: string;
}

function UserManagement() {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [editingUser, setEditingUser] = useState<UserProfile | null>(null);
  const [formData, setFormData] = useState<UserProfile>({
    firstName: '',
    lastName: '',
    email: '',
    department: ''
  });

  const openDrawer = (user?: UserProfile) => {
    if (user) {
      setEditingUser(user);
      setFormData(user);
    } else {
      setEditingUser(null);
      setFormData({ firstName: '', lastName: '', email: '', department: '' });
    }
    setDrawerOpen(true);
  };

  const closeDrawer = () => {
    setDrawerOpen(false);
    setEditingUser(null);
  };

  const handleSave = () => {
    if (editingUser) {
      console.log('Updating user:', formData);
    } else {
      console.log('Creating user:', formData);
    }
    closeDrawer();
  };

  const updateFormField = (field: keyof UserProfile) => (value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  return (
    <div>
      {/* Main page content */}
      <GoabBlock direction="row" gap="m" alignment="center" mb="l">
        <h1>User Management</h1>
        <GoabButton type="primary" onClick={() => openDrawer()}>
          Add User
        </GoabButton>
      </GoabBlock>

      {/* User list would go here */}
      <div className="user-list">
        <p>User list with edit buttons that call openDrawer(user)</p>
      </div>

      {/* Form Drawer */}
      <GoabDrawer
        open={drawerOpen}
        position="right"
        heading={editingUser ? 'Edit User' : 'Add New User'}
        onClose={closeDrawer}
        testId="user-form-drawer"
      >
        <GoabBlock direction="column" gap="l">
          <GoabFormItem label="First Name">
            <GoabInput
              name="firstName"
              value={formData.firstName}
              onChange={(e) => updateFormField('firstName')(e.value)}
            />
          </GoabFormItem>

          <GoabFormItem label="Last Name">
            <GoabInput
              name="lastName"
              value={formData.lastName}
              onChange={(e) => updateFormField('lastName')(e.value)}
            />
          </GoabFormItem>

          <GoabFormItem label="Email">
            <GoabInput
              type="email"
              name="email"
              value={formData.email}
              onChange={(e) => updateFormField('email')(e.value)}
            />
          </GoabFormItem>

          <GoabFormItem label="Department">
            <GoabInput
              name="department"
              value={formData.department}
              onChange={(e) => updateFormField('department')(e.value)}
            />
          </GoabFormItem>
        </GoabBlock>

        {/* Drawer Actions */}
        <div slot="actions">
          <GoabBlock direction="row" gap="m">
            <GoabButton type="primary" onClick={handleSave}>
              {editingUser ? 'Update User' : 'Create User'}
            </GoabButton>
            <GoabButton type="secondary" onClick={closeDrawer}>
              Cancel
            </GoabButton>
          </GoabBlock>
        </div>
      </GoabDrawer>
    </div>
  );
}

import React, { useState } from 'react';
import { GoabDrawer, GoabFormItem, GoabDropdown, GoabDropdownItem, GoabCheckbox, GoabButton, GoabBlock } from '@abgov/react-components';

interface FilterOptions {
  status: string;
  department: string;
  dateRange: string;
  includeArchived: boolean;
}

function DataTableWithFilters() {
  const [filtersOpen, setFiltersOpen] = useState(false);
  const [filters, setFilters] = useState<FilterOptions>({
    status: '',
    department: '',
    dateRange: '',
    includeArchived: false
  });
  const [appliedFilters, setAppliedFilters] = useState<FilterOptions>(filters);

  const applyFilters = () => {
    setAppliedFilters(filters);
    console.log('Applying filters:', filters);
    setFiltersOpen(false);
  };

  const resetFilters = () => {
    const emptyFilters = {
      status: '',
      department: '',
      dateRange: '',
      includeArchived: false
    };
    setFilters(emptyFilters);
    setAppliedFilters(emptyFilters);
  };

  const closeFilters = () => {
    // Reset to applied filters if user cancels
    setFilters(appliedFilters);
    setFiltersOpen(false);
  };

  const activeFilterCount = Object.values(appliedFilters).filter(
    (value) => value && value !== ''
  ).length;

  return (
    <div>
      {/* Main content header with filter button */}
      <GoabBlock direction="row" gap="m" alignment="center" mb="l">
        <h1>Application List</h1>
        <GoabButton 
          type="secondary" 
          onClick={() => setFiltersOpen(true)}
        >
          Filters {activeFilterCount > 0 && `(${activeFilterCount})`}
        </GoabButton>
      </GoabBlock>

      {/* Data table would go here */}
      <div className="data-table">
        <p>Data table with applied filters: {JSON.stringify(appliedFilters)}</p>
      </div>

      {/* Filter Drawer */}
      <GoabDrawer
        open={filtersOpen}
        position="right"
        heading="Filter Applications"
        onClose={closeFilters}
        maxSize="400px"
        testId="filters-drawer"
      >
        <GoabBlock direction="column" gap="l">
          <GoabFormItem label="Status">
            <GoabDropdown
              name="status"
              value={filters.status}
              onChange={(e) => setFilters(prev => ({...prev, status: e.value}))}
              placeholder="All statuses"
            >
              <GoabDropdownItem value="pending">Pending</GoabDropdownItem>
              <GoabDropdownItem value="approved">Approved</GoabDropdownItem>
              <GoabDropdownItem value="rejected">Rejected</GoabDropdownItem>
            </GoabDropdown>
          </GoabFormItem>

          <GoabFormItem label="Department">
            <GoabDropdown
              name="department"
              value={filters.department}
              onChange={(e) => setFilters(prev => ({...prev, department: e.value}))}
              placeholder="All departments"
            >
              <GoabDropdownItem value="finance">Finance</GoabDropdownItem>
              <GoabDropdownItem value="hr">Human Resources</GoabDropdownItem>
              <GoabDropdownItem value="it">Information Technology</GoabDropdownItem>
            </GoabDropdown>
          </GoabFormItem>

          <GoabFormItem label="Date Range">
            <GoabDropdown
              name="dateRange"
              value={filters.dateRange}
              onChange={(e) => setFilters(prev => ({...prev, dateRange: e.value}))}
              placeholder="Any time"
            >
              <GoabDropdownItem value="today">Today</GoabDropdownItem>
              <GoabDropdownItem value="week">Last 7 days</GoabDropdownItem>
              <GoabDropdownItem value="month">Last 30 days</GoabDropdownItem>
            </GoabDropdown>
          </GoabFormItem>

          <GoabFormItem label="Additional Options">
            <GoabCheckbox
              name="includeArchived"
              checked={filters.includeArchived}
              onChange={(e) => setFilters(prev => ({...prev, includeArchived: e.checked}))}
            >
              Include archived items
            </GoabCheckbox>
          </GoabFormItem>
        </GoabBlock>

        {/* Filter Actions */}
        <div slot="actions">
          <GoabBlock direction="row" gap="m">
            <GoabButton type="primary" onClick={applyFilters}>
              Apply Filters
            </GoabButton>
            <GoabButton type="secondary" onClick={resetFilters}>
              Reset
            </GoabButton>
          </GoabBlock>
        </div>
      </GoabDrawer>
    </div>
  );
}

