import { Component } from '@angular/core';

interface UserSettings {
  notifications: boolean;
  theme: 'light' | 'dark';
  language: string;
  autoSave: boolean;
}

@Component({
  selector: 'app-settings-manager',
  templateUrl: './settings-manager.component.html'
})
export class SettingsManagerComponent {
  settingsOpen = false;

  settings: UserSettings = {
    notifications: true,
    theme: 'light',
    language: 'en',
    autoSave: false
  };

  originalSettings: UserSettings = {...this.settings};

  openSettings() {
    this.originalSettings = {...this.settings};
    this.settingsOpen = true;
  }

  closeSettings() {
    // Restore original settings if cancelled
    this.settings = {...this.originalSettings};
    this.settingsOpen = false;
  }

  saveSettings() {
    console.log('Saving settings:', this.settings);
    // API call would go here
    this.settingsOpen = false;
  }

  onNotificationChange(event: {checked: boolean}) {
    this.settings.notifications = event.checked;
  }

  onThemeChange(event: {value: string}) {
    this.settings.theme = event.value as 'light' | 'dark';
  }

  onLanguageChange(event: {value: string}) {
    this.settings.language = event.value;
  }

  onAutoSaveChange(event: {checked: boolean}) {
    this.settings.autoSave = event.checked;
  }
}
