function SingleFileUpload() {
  const [selectedFile, setSelectedFile] = React.useState(null);
  const [uploadProgress, setUploadProgress] = React.useState(0);
  const [uploadError, setUploadError] = React.useState(null);

  const simulateUpload = (file) => {
    setUploadProgress(0);
    setUploadError(null);

    const interval = setInterval(() => {
      setUploadProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          return 100;
        }
        return prev + 10;
      });
    }, 200);
  };

  const handleFileSelect = (event) => {
    const file = event.file;

    // Validate file size (example: 5MB limit)
    if (file.size > 5 * 1024 * 1024) {
      setUploadError('File size must be less than 5MB');
      return;
    }

    setSelectedFile(file);
    simulateUpload(file);
  };

  const handleDelete = () => {
    setSelectedFile(null);
    setUploadProgress(0);
    setUploadError(null);
  };

  return (
    <GoabBlock gap="m" direction="column">
      <GoabFormItem label="Upload Document" error={uploadError}>
        <GoabFileUploadInput
          variant="dragdrop"
          maxFileSize="5MB"
          accept=".pdf,.doc,.docx"
          onSelectFile={handleFileSelect}
        />
      </GoabFormItem>

      {selectedFile && (
        <GoabFileUploadCard
          filename={selectedFile.name}
          type={selectedFile.type}
          size={selectedFile.size}
          progress={uploadProgress}
          error={uploadError}
          onDelete={handleDelete}
          onCancel={handleDelete}
        />
      )}
    </GoabBlock>
  );
}

function MultiFileUpload() {
  const [uploads, setUploads] = React.useState([]);
  const [progressList, setProgressList] = React.useState({});
  const maxFiles = 3;

  const getAcceptType = (typeFilter) => {
    const types = {
      'All Files': '*',
      'Documents': '.pdf,.doc,.docx,.txt',
      'Images': 'image/*',
      'PDFs Only': '.pdf'
    };
    return types[typeFilter] || '*';
  };

  const simulateUpload = (uploadId) => {
    let progress = 0;
    const interval = setInterval(() => {
      progress += Math.random() * 20;
      if (progress >= 100) {
        progress = 100;
        clearInterval(interval);
      }
      setProgressList(prev => ({ ...prev, [uploadId]: Math.floor(progress) }));
    }, 300);
  };

  const handleFileSelect = (event) => {
    const file = event.file;

    // Check file limit
    if (uploads.length >= maxFiles) {
      alert(`Maximum ${maxFiles} files allowed`);
      return;
    }

    // Check for duplicates
    const isDuplicate = uploads.some(upload => 
      upload.file.name === file.name && upload.file.size === file.size
    );
    if (isDuplicate) {
      alert('File already selected');
      return;
    }

    const uploadId = `${file.name}-${Date.now()}`;

    // Add to uploads list
    setUploads(prev => [...prev, { id: uploadId, file }]);

    // Start simulated upload
    simulateUpload(uploadId);
  };

  const removeUpload = (uploadId) => {
    setUploads(prev => prev.filter(upload => upload.id !== uploadId));
    setProgressList(prev => {
      const newProgress = { ...prev };
      delete newProgress[uploadId];
      return newProgress;
    });
  };

  return (
    <GoabBlock gap="m" direction="column">
      <GoabFormItem label={`Upload Files (${uploads.length}/${maxFiles})`}>
        <GoabFileUploadInput
          variant="dragdrop"
          maxFileSize="10MB"
          accept={getAcceptType('Documents')}
          onSelectFile={handleFileSelect}
        />
      </GoabFormItem>

      {uploads.length > 0 && (
        <GoabBlock gap="s" direction="column">
          <GoabText size="s" color="secondary">
            Uploaded Files:
          </GoabText>
          {uploads.map(upload => (
            <GoabFileUploadCard
              key={upload.id}
              filename={upload.file.name}
              type={upload.file.type}
              size={upload.file.size}
              progress={progressList[upload.id] || 0}
              onDelete={() => removeUpload(upload.id)}
              onCancel={() => removeUpload(upload.id)}
            />
          ))}
        </GoabBlock>
      )}

      {uploads.length > 0 && (
        <GoabButton
          type="primary"
          disabled={Object.values(progressList).some(p => p < 100)}
        >
          Submit ({uploads.length} files)
        </GoabButton>
      )}
    </GoabBlock>
  );
}

function DocumentForm() {
  const [formData, setFormData] = React.useState({
    name: '',
    email: '',
    documents: []
  });
  const [uploadProgress, setUploadProgress] = React.useState({});
  const [errors, setErrors] = React.useState({});

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (formData.documents.length === 0) {
      newErrors.documents = 'At least one document is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleFileSelect = (event) => {
    const file = event.file;
    const uploadId = Date.now().toString();

    // Simulate upload
    let progress = 0;
    const interval = setInterval(() => {
      progress += 15;
      if (progress >= 100) {
        progress = 100;
        clearInterval(interval);

        // Add to form data when upload completes
        setFormData(prev => ({
          ...prev,
          documents: [...prev.documents, {
            id: uploadId,
            filename: file.name,
            size: file.size,
            type: file.type,
            url: `https://example.com/files/${uploadId}`
          }]
        }));
      }
      setUploadProgress(prev => ({ ...prev, [uploadId]: progress }));
    }, 200);

    // Clear documents error when file is selected
    if (errors.documents) {
      setErrors(prev => ({ ...prev, documents: null }));
    }
  };

  const removeDocument = (docId) => {
    setFormData(prev => ({
      ...prev,
      documents: prev.documents.filter(doc => doc.id !== docId)
    }));
    setUploadProgress(prev => {
      const newProgress = { ...prev };
      delete newProgress[docId];
      return newProgress;
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!validateForm()) {
      return;
    }

    // Check if any uploads are still in progress
    const uploadsInProgress = Object.values(uploadProgress).some(p => p < 100);
    if (uploadsInProgress) {
      alert('Please wait for all uploads to complete');
      return;
    }

    console.log('Submitting form:', formData);
    alert('Form submitted successfully!');
  };

  return (
    <form onSubmit={handleSubmit}>
      <GoabBlock gap="l" direction="column">
        <GoabFormItem label="Name" error={errors.name}>
          <GoabInput
            value={formData.name}
            onChange={(name, value) => setFormData(prev => ({ ...prev, name: value }))}
            placeholder="Enter your name"
          />
        </GoabFormItem>

        <GoabFormItem label="Email" error={errors.email}>
          <GoabInput
            type="email"
            value={formData.email}
            onChange={(name, value) => setFormData(prev => ({ ...prev, email: value }))}
            placeholder="Enter your email"
          />
        </GoabFormItem>

        <GoabFormItem label="Required Documents" error={errors.documents}>
          <GoabFileUploadInput
            variant="dragdrop"
            maxFileSize="10MB"
            accept=".pdf,.doc,.docx"
            onSelectFile={handleFileSelect}
          />

          {formData.documents.map(doc => (
            <GoabFileUploadCard
              key={doc.id}
              filename={doc.filename}
              size={doc.size}
              progress={uploadProgress[doc.id] || 100}
              onDelete={() => removeDocument(doc.id)}
            />
          ))}
        </GoabFormItem>

        <GoabButtonGroup alignment="end">
          <GoabButton type="secondary" onClick={() => setFormData({ name: '', email: '', documents: [] })}>
            Reset
          </GoabButton>
          <GoabButton 
            type="primary" 
            submit
            disabled={Object.values(uploadProgress).some(p => p < 100)}
          >
            Submit Application
          </GoabButton>
        </GoabButtonGroup>
      </GoabBlock>
    </form>
  );
}

function ImageUploadWithPreview() {
  const [images, setImages] = React.useState([]);
  const [previews, setPreviews] = React.useState({});
  const [uploadProgress, setUploadProgress] = React.useState({});
  const maxImages = 3;

  const createImagePreview = (file, imageId) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      setPreviews(prev => ({ ...prev, [imageId]: e.target.result }));
    };
    reader.readAsDataURL(file);
  };

  const validateImage = (file) => {
    // Check file type
    if (!file.type.startsWith('image/')) {
      return 'File must be an image';
    }

    // Check file size (5MB limit)
    if (file.size > 5 * 1024 * 1024) {
      return 'Image must be less than 5MB';
    }

    return null;
  };

  const handleImageSelect = (event) => {
    const file = event.file;

    // Validate image
    const validationError = validateImage(file);
    if (validationError) {
      alert(validationError);
      return;
    }

    // Check image limit
    if (images.length >= maxImages) {
      alert(`Maximum ${maxImages} images allowed`);
      return;
    }

    const imageId = `${file.name}-${Date.now()}`;

    // Add to images list
    setImages(prev => [...prev, { id: imageId, file }]);

    // Create preview
    createImagePreview(file, imageId);

    // Simulate upload
    let progress = 0;
    const interval = setInterval(() => {
      progress += 12;
      if (progress >= 100) {
        progress = 100;
        clearInterval(interval);
      }
      setUploadProgress(prev => ({ ...prev, [imageId]: progress }));
    }, 150);
  };

  const removeImage = (imageId) => {
    setImages(prev => prev.filter(img => img.id !== imageId));
    setPreviews(prev => {
      const newPreviews = { ...prev };
      delete newPreviews[imageId];
      return newPreviews;
    });
    setUploadProgress(prev => {
      const newProgress = { ...prev };
      delete newProgress[imageId];
      return newProgress;
    });
  };

  return (
    <GoabBlock gap="l" direction="column">
      <GoabFormItem label={`Upload Images (${images.length}/${maxImages})`}>
        <GoabFileUploadInput
          variant="dragdrop"
          maxFileSize="5MB"
          accept="image/*"
          onSelectFile={handleImageSelect}
        />
      </GoabFormItem>

      {images.length > 0 && (
        <GoabBlock gap="m" direction="column">
          <GoabText size="s" color="secondary">
            Selected Images:
          </GoabText>

          <div style={{ 
            display: 'grid', 
            gap: '1rem', 
            gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))' 
          }}>
            {images.map(image => (
              <GoabBlock key={image.id} gap="s" direction="column">
                {previews[image.id] && (
                  <div style={{
                    width: '100%',
                    height: '150px',
                    backgroundColor: '#f5f5f5',
                    borderRadius: '4px',
                    overflow: 'hidden',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center'
                  }}>
                    <img
                      src={previews[image.id]}
                      alt={image.file.name}
                      style={{
                        maxWidth: '100%',
                        maxHeight: '100%',
                        objectFit: 'cover'
                      }}
                    />
                  </div>
                )}

                <GoabFileUploadCard
                  filename={image.file.name}
                  type={image.file.type}
                  size={image.file.size}
                  progress={uploadProgress[image.id] || 0}
                  onDelete={() => removeImage(image.id)}
                  onCancel={() => removeImage(image.id)}
                />
              </GoabBlock>
            ))}
          </div>
        </GoabBlock>
      )}

      {images.length > 0 && (
        <GoabCallout type="information">
          <GoabText size="s">
            Images will be automatically resized and optimized for web display.
            Original files are preserved for download.
          </GoabText>
        </GoabCallout>
      )}
    </GoabBlock>
  );
}

function FileTypeDemo() {
  const [selectedFilter, setSelectedFilter] = React.useState('Documents');
  const [strictValidation, setStrictValidation] = React.useState(false);
  const [selectedFiles, setSelectedFiles] = React.useState([]);
  const [validationErrors, setValidationErrors] = React.useState([]);

  const fileTypeConfigs = {
    'All Files': {
      accept: '*',
      description: 'Any file type',
      extensions: ['*'],
      mimeTypes: ['*']
    },
    'Documents': {
      accept: '.pdf,.doc,.docx,.txt,.rtf',
      description: 'PDF, Word, and text documents',
      extensions: ['.pdf', '.doc', '.docx', '.txt', '.rtf'],
      mimeTypes: [
        'application/pdf',
        'application/msword',
        'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
        'text/plain',
        'application/rtf'
      ]
    },
    'Images': {
      accept: 'image/*',
      description: 'JPEG, PNG, GIF, WebP images',
      extensions: ['.jpg', '.jpeg', '.png', '.gif', '.webp'],
      mimeTypes: ['image/jpeg', 'image/png', 'image/gif', 'image/webp']
    },
    'Archives': {
      accept: '.zip,.rar,.7z,.tar,.gz',
      description: 'Compressed archive files',
      extensions: ['.zip', '.rar', '.7z', '.tar', '.gz'],
      mimeTypes: [
        'application/zip',
        'application/x-rar-compressed',
        'application/x-7z-compressed',
        'application/x-tar',
        'application/gzip'
      ]
    },
    'Custom': {
      accept: '.json,.xml,.csv',
      description: 'Data files (JSON, XML, CSV)',
      extensions: ['.json', '.xml', '.csv'],
      mimeTypes: ['application/json', 'application/xml', 'text/csv']
    }
  };

  const validateFileType = (file, config) => {
    if (!strictValidation || config.accept === '*') return null;

    const fileName = file.name.toLowerCase();
    const fileType = file.type.toLowerCase();

    // Check file extension
    const hasValidExtension = config.extensions.some(ext => 
      ext === '*' || fileName.endsWith(ext.toLowerCase())
    );

    // Check MIME type
    const hasValidMimeType = config.mimeTypes.some(mime => 
      mime === '*' || fileType === mime || fileType.startsWith(mime.split('/')[0] + '/')
    );

    if (!hasValidExtension && !hasValidMimeType) {
      return `File type not allowed. Expected: ${config.description}`;
    }

    return null;
  };

  const handleFileSelect = (event) => {
    const file = event.file;
    const config = fileTypeConfigs[selectedFilter];

    // Validate file type
    const validationError = validateFileType(file, config);
    if (validationError) {
      setValidationErrors(prev => [...prev, {
        filename: file.name,
        error: validationError,
        timestamp: Date.now()
      }]);
      return;
    }

    // Add file to selected list
    setSelectedFiles(prev => [...prev, {
      id: Date.now().toString(),
      file,
      addedAt: new Date().toLocaleTimeString()
    }]);

    // Clear any previous validation errors for this file
    setValidationErrors(prev => 
      prev.filter(err => err.filename !== file.name)
    );
  };

  const removeFile = (fileId) => {
    setSelectedFiles(prev => prev.filter(f => f.id !== fileId));
  };

  const clearErrors = () => {
    setValidationErrors([]);
  };

  const currentConfig = fileTypeConfigs[selectedFilter];

  return (
    <GoabBlock gap="l" direction="column">
      <GoabBlock gap="m" direction="column">
        <GoabText size="m" weight="bold">
          File Type Configuration
        </GoabText>

        <GoabFormItem label="File Type Filter">
          <GoabDropdown
            value={selectedFilter}
            onChange={(name, value) => {
              setSelectedFilter(value);
              setValidationErrors([]);
            }}
          >
            {Object.keys(fileTypeConfigs).map(type => (
              <GoabDropdownItem key={type} value={type}>
                {type}
              </GoabDropdownItem>
            ))}
          </GoabDropdown>
        </GoabFormItem>

        <GoabFormItem>
          <GoabCheckbox
            checked={strictValidation}
            onChange={(name, checked) => setStrictValidation(checked)}
          >
            Enable strict validation (server-side style)
          </GoabCheckbox>
        </GoabFormItem>

        <GoabCallout type="information">
          <GoabBlock gap="xs" direction="column">
            <GoabText size="s" weight="bold">
              Current Configuration:
            </GoabText>
            <GoabText size="s">
              <strong>Accept:</strong> {currentConfig.accept}
            </GoabText>
            <GoabText size="s">
              <strong>Description:</strong> {currentConfig.description}
            </GoabText>
            <GoabText size="s">
              <strong>Extensions:</strong> {currentConfig.extensions.join(', ')}
            </GoabText>
            <GoabText size="s">
              <strong>Strict Validation:</strong> {strictValidation ? 'Enabled' : 'Disabled'}
            </GoabText>
          </GoabBlock>
        </GoabCallout>
      </GoabBlock>

      <GoabFormItem label="Upload Files">
        <GoabFileUploadInput
          variant="dragdrop"
          maxFileSize="10MB"
          accept={currentConfig.accept}
          onSelectFile={handleFileSelect}
        />
      </GoabFormItem>

      {validationErrors.length > 0 && (
        <GoabBlock gap="s" direction="column">
          <GoabBlock gap="xs" direction="row" alignment="center">
            <GoabText size="s" color="error" weight="bold">
              Validation Errors:
            </GoabText>
            <GoabButton type="tertiary" size="compact" onClick={clearErrors}>
              Clear All
            </GoabButton>
          </GoabBlock>

          {validationErrors.map(error => (
            <GoabCallout key={error.timestamp} type="error">
              <GoabText size="s">
                <strong>{error.filename}:</strong> {error.error}
              </GoabText>
            </GoabCallout>
          ))}
        </GoabBlock>
      )}

      {selectedFiles.length > 0 && (
        <GoabBlock gap="s" direction="column">
          <GoabText size="s" color="secondary">
            Successfully Selected Files:
          </GoabText>

          {selectedFiles.map(fileInfo => (
            <GoabBlock key={fileInfo.id} gap="xs" direction="row" alignment="center">
              <GoabText size="s">
                üìÑ {fileInfo.file.name} ({(fileInfo.file.size / 1024).toFixed(1)}KB)
              </GoabText>
              <GoabText size="xs" color="secondary">
                Added: {fileInfo.addedAt}
              </GoabText>
              <GoabButton 
                type="tertiary" 
                size="compact" 
                onClick={() => removeFile(fileInfo.id)}
              >
                Remove
              </GoabButton>
            </GoabBlock>
          ))}
        </GoabBlock>
      )}
    </GoabBlock>
  );
}

