function SimpleUpload() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleFileSelect = (event) => {
    const file = event.file;
    setSelectedFile(file);

    // Simulate upload progress
    const uploader = simulateUpload(file);
    uploader.onProgress = (progress) => setUploadProgress(progress);
  };

  const handleDelete = () => {
    setSelectedFile(null);
    setUploadProgress(0);
  };

  return (
    <GoabFormItem label="Upload Document">
      <GoabFileUploadInput 
        variant="dragdrop"
        accept=".pdf,.doc,.docx"
        maxFileSize="10MB"
        onSelectFile={handleFileSelect}
      />
      {selectedFile && (
        <GoabFileUploadCard
          filename={selectedFile.name}
          type={selectedFile.type}
          size={selectedFile.size}
          progress={uploadProgress}
          onDelete={handleDelete}
          onCancel={handleDelete}
        />
      )}
    </GoabFormItem>
  );
}

function MultiFileUpload() {
  const [uploads, setUploads] = useState([]);
  const [progressList, setProgressList] = useState({});

  const handleFileSelect = (event) => {
    const file = event.file;
    const uploadId = `${file.name}-${Date.now()}`;

    // Add to uploads list
    setUploads(prev => [...prev, { id: uploadId, file }]);

    // Start upload with progress tracking
    const uploader = new FileUploader(file);
    uploader.onProgress = (progress) => {
      setProgressList(prev => ({ ...prev, [uploadId]: progress }));
    };
    uploader.onComplete = () => {
      setProgressList(prev => ({ ...prev, [uploadId]: 100 }));
    };
    uploader.onError = (error) => {
      setUploads(prev => prev.map(upload => 
        upload.id === uploadId 
          ? { ...upload, error: error.message }
          : upload
      ));
    };

    uploader.start();
  };

  const removeUpload = (uploadId) => {
    setUploads(prev => prev.filter(upload => upload.id !== uploadId));
    setProgressList(prev => {
      const newProgress = { ...prev };
      delete newProgress[uploadId];
      return newProgress;
    });
  };

  return (
    <GoabFormItem label="Upload Multiple Files">
      <GoabFileUploadInput 
        variant="dragdrop"
        accept="image/*,.pdf"
        maxFileSize="5MB"
        onSelectFile={handleFileSelect}
      />
      {uploads.map(upload => (
        <GoabFileUploadCard
          key={upload.id}
          filename={upload.file.name}
          type={upload.file.type}
          size={upload.file.size}
          progress={progressList[upload.id] || 0}
          error={upload.error}
          onDelete={() => removeUpload(upload.id)}
          onCancel={() => removeUpload(upload.id)}
        />
      ))}
    </GoabFormItem>
  );
}

