export class FileUploadComponent {
  uploads: Array<{id: string, file: File, error?: string}> = [];
  progressList: Record<string, number> = {};

  handleFileSelect(event: any) {
    const file = event.file;
    const uploadId = `${file.name}-${Date.now()}`;

    this.uploads.push({ id: uploadId, file });

    // Start upload process
    const uploader = new FileUploader(file);
    uploader.onProgress = (progress: number) => {
      this.progressList[uploadId] = progress;
    };
    uploader.start();
  }

  removeUpload(uploadId: string) {
    this.uploads = this.uploads.filter(upload => upload.id !== uploadId);
    delete this.progressList[uploadId];
  }
}

