// React form integration example
function DocumentForm() {
  const [formData, setFormData] = useState({ documents: [] });
  const [uploadProgress, setUploadProgress] = useState({});

  const handleFileSelect = async (event) => {
    const file = event.file;
    const uploadId = Date.now().toString();

    try {
      // Upload file to server
      const uploadedFile = await uploadFileToServer(file, (progress) => {
        setUploadProgress(prev => ({ ...prev, [uploadId]: progress }));
      });

      // Add to form data
      setFormData(prev => ({
        ...prev,
        documents: [...prev.documents, { id: uploadId, ...uploadedFile }]
      }));
    } catch (error) {
      console.error('Upload failed:', error);
    }
  };

  const handleSubmit = () => {
    // Submit form with document references
    submitForm({ ...formData, documents: formData.documents });
  };

  return (
    <form onSubmit={handleSubmit}>
      <GoabFormItem label="Required Documents">
        <GoabFileUploadInput 
          variant="dragdrop"
          accept=".pdf"
          maxFileSize="10MB"
          onSelectFile={handleFileSelect}
        />
        {formData.documents.map(doc => (
          <GoabFileUploadCard
            key={doc.id}
            filename={doc.filename}
            progress={uploadProgress[doc.id] || 100}
            onDelete={() => removeDocument(doc.id)}
          />
        ))}
      </GoabFormItem>
      <GoabButton type="primary" disabled={formData.documents.length === 0}>
        Submit Application
      </GoabButton>
    </form>
  );
}

function ImageUpload() {
  const [images, setImages] = useState([]);
  const [previews, setPreviews] = useState({});

  const handleImageSelect = (event) => {
    const file = event.file;
    const imageId = Date.now().toString();

    // Create preview
    const reader = new FileReader();
    reader.onload = (e) => {
      setPreviews(prev => ({ ...prev, [imageId]: e.target.result }));
    };
    reader.readAsDataURL(file);

    // Add to images list
    setImages(prev => [...prev, { id: imageId, file }]);
  };

  return (
    <div>
      <GoabFileUploadInput 
        variant="dragdrop"
        accept="image/*"
        maxFileSize="5MB"
        onSelectFile={handleImageSelect}
      />

      <div style={{ display: 'grid', gap: '1rem', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))' }}>
        {images.map(image => (
          <div key={image.id}>
            {previews[image.id] && (
              <img 
                src={previews[image.id]} 
                alt={image.file.name}
                style={{ width: '100%', height: '150px', objectFit: 'cover' }}
              />
            )}
            <GoabFileUploadCard
              filename={image.file.name}
              size={image.file.size}
              progress={100} // Assume immediate preview
              onDelete={() => removeImage(image.id)}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

