}

commonPatterns.code:
// React - File download list
function FileDownloads({ files }) {
  return (
    <div className="file-list">
      {files.map((file, index) => (
        <div key={index} className="file-item">
          <GoabLink leadingIcon="document-text" mb="xs">
            <a 
              href={file.url} 
              download={file.name}
              aria-label={`Download ${file.name} (${file.size})`}
            >
              {file.name}
            </a>
          </GoabLink>
          <span className="file-meta">{file.size} • {file.type}</span>
        </div>
      ))}
    </div>
  );
}

// React - Breadcrumb with enhanced links
function Breadcrumb({ items }) {
  return (
    <nav aria-label="Breadcrumb">
      <ol className="breadcrumb-list">
        {items.map((item, index) => (
          <li key={index}>
            {index < items.length - 1 ? (
              <>
                <GoabLink mr="xs">
                  <a href={item.href}>{item.label}</a>
                </GoabLink>
                <span className="breadcrumb-separator" aria-hidden="true">/</span>
              </>
            ) : (
              <span aria-current="page">{item.label}</span>
            )}
          </li>
        ))}
      </ol>
    </nav>
  );
}

commonMistakes.example:
// ❌ Wrong
<GoabLink trailingIcon="open">External Site</GoabLink>

// ✅ Correct
<GoabLink trailingIcon="open">
  <a href="https://external.com">External Site</a>
</GoabLink>

// ❌ Wrong
<GoabLink onClick={handleSubmit}>Submit Form</GoabLink>

// ✅ Correct
<GoabButton onClick={handleSubmit}>Submit Form</GoabButton>

// ❌ Wrong
<GoabLink><a href="/report.pdf">Click here</a></GoabLink>

// ✅ Correct
<GoabLink leadingIcon="download">
  <a href="/report.pdf">Download 2024 Annual Report (PDF)</a>
</GoabLink>
