commonIssues.example:
// Correct
<GoabSideMenuHeading href="/admin/users">
  User Management
</GoabSideMenuHeading>

// URL matching is exact, so /admin/users/edit won't match /admin/users

commonMistakes.example:
// ❌ Wrong - no href for navigation
<GoabSideMenuHeading>User Management</GoabSideMenuHeading>

// ✅ Correct - href for navigation
<GoabSideMenuHeading href="/admin/users">User Management</GoabSideMenuHeading>

// ❌ Wrong - no mobile consideration
<aside className="sidebar">
  <GoabSideMenu>...</GoabSideMenu>
</aside>

// ✅ Correct - responsive planning
<aside className={`sidebar ${isMobile ? 'mobile-hidden' : ''}`}>
  <GoabSideMenu>...</GoabSideMenu>
</aside>
// Consider hamburger menu or drawer pattern for mobile
