basicExample.code:
// src/App.tsx
import React from 'react';
import { GoabButton, GoabContainer, GoabFormItem, GoabInput } from '@abgov/react-components';
import '@abgov/web-components';

function App() {
  const [name, setName] = React.useState('');

  return (
    <div className="App">
      <GoabContainer accent="thick" title="Welcome">
        <GoabFormItem label="Your Name">
          <GoabInput 
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter your name"
          />
        </GoabFormItem>

        <GoabButton type="primary" mt="m">
          Submit
        </GoabButton>
      </GoabContainer>
    </div>
  );
}

export default App;

