// React - Content width based on page context
function AdaptiveLayout({ contentType, children }) {
  const getPageWidth = () => {
    switch(contentType) {
      case 'article': case 'form': return '704px'; // Optimal reading/form width
      case 'wide-form': return '960px'; // Wider forms with multiple columns
      case 'dashboard': case 'full': return 'full'; // Full width for data/dashboards
      default: return '704px';
    }
  };

  return (
    <GoabOneColumnLayout>
      <section slot="header">
        <GoabAppHeader heading="My App" />
      </section>

      <GoabPageBlock width={getPageWidth()}>
        {children}
      </GoabPageBlock>

      <section slot="footer">
        <GoabAppFooter />
      </section>
    </GoabOneColumnLayout>
  );
}

// React - Conditional microsite header
function ConditionalHeaderLayout({ showMicrosite, environment, children }) {
  return (
    <GoabOneColumnLayout>
      <section slot="header">
        {showMicrosite && (
          <GoabMicrositeHeader 
            type={environment === 'production' ? 'live' : 'alpha'} 
            version={environment.toUpperCase()} 
          />
        )}
        <GoabAppHeader heading="My Application" url="/">
          <a href="/login">Sign in</a>
        </GoabAppHeader>
      </section>

      <GoabPageBlock width="704px">
        {children}
      </GoabPageBlock>

      <section slot="footer">
        <GoabAppFooter />
      </section>
    </GoabOneColumnLayout>
  );
}

