const [feedbackModal, setFeedbackModal] = useState(false);
const [serviceType, setServiceType] = useState<'alpha' | 'beta' | 'live'>('alpha');
const [feedbackData, setFeedbackData] = useState(null);

const feedbackConfig = {
  alpha: {
    message: 'This is an alpha service. Your feedback helps us improve.',
    feedbackUrl: 'https://alberta.ca/feedback/alpha',
    showFeedback: true
  },
  beta: {
    message: 'This is a beta service. Please share your experience.',
    feedbackUrl: 'https://alberta.ca/feedback/beta',
    showFeedback: true
  },
  live: {
    message: 'This is a live service.',
    feedbackUrl: 'https://alberta.ca/feedback/general',
    showFeedback: false
  }
};

const handleFeedbackClick = () => {
  console.log('Feedback clicked for service type:', serviceType);

  const config = feedbackConfig[serviceType];

  // Track feedback interaction
  trackFeedbackInteraction(serviceType);

  // Handle feedback based on service type
  if (config.feedbackUrl) {
    window.open(config.feedbackUrl, '_blank');
  } else {
    setFeedbackModal(true);
  }
};

const trackFeedbackInteraction = (type: string) => {
  // Send analytics event
  console.log(`Feedback interaction tracked for ${type} service`);
  // analytics.track('feedback_clicked', { service_type: type });
};

const handleFeedbackSubmit = (data: any) => {
  setFeedbackData(data);
  console.log('Feedback submitted:', data);
  // Process feedback submission
  // Send to feedback collection service
};

const shouldShowFeedback = () => {
  return feedbackConfig[serviceType].showFeedback;
};

const getServiceMessage = () => {
  return feedbackConfig[serviceType].message;
};

<div className="feedback-service-example">
  <GoabMicrositeHeader 
    type={serviceType} 
    onFeedbackClick={shouldShowFeedback() ? handleFeedbackClick : undefined} 
  />

  <main className="service-content">
    <div className="service-status">
      <p>{getServiceMessage()}</p>
    </div>

    <h1>Government Service Application</h1>

    {feedbackModal && (
      <div className="feedback-modal">
        <div className="modal-content">
          <h2>Provide Feedback</h2>
          <form onSubmit={(e) => {
            e.preventDefault();
            handleFeedbackSubmit(new FormData(e.target));
            setFeedbackModal(false);
          }}>
            <textarea 
              name="feedback" 
              placeholder="Tell us about your experience..."
              required
            />
            <button type="submit">Submit Feedback</button>
            <button type="button" onClick={() => setFeedbackModal(false)}>Cancel</button>
          </form>
        </div>
      </div>
    )}
  </main>
</div>

/* CSS */
.feedback-service-example {
  width: 100%;
}

.service-content {
  padding: var(--goa-space-l);
  max-width: 1200px;
  margin: 0 auto;
}

.service-status {
  background-color: var(--goa-color-info-light);
  padding: var(--goa-space-m);
  border-radius: var(--goa-border-radius-s);
  margin-bottom: var(--goa-space-l);
}

.feedback-confirmation {
  background-color: var(--goa-color-success-light);
  padding: var(--goa-space-m);
  border-radius: var(--goa-border-radius-s);
  margin-bottom: var(--goa-space-l);
  border-left: 4px solid var(--goa-color-success);
}

.feedback-stats {
  font-size: var(--goa-font-size-s);
  color: var(--goa-color-text-secondary);
  margin-bottom: var(--goa-space-l);
}

.feedback-modal {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
}

.modal-content {
  background: white;
  padding: var(--goa-space-xl);
  border-radius: var(--goa-border-radius-m);
  max-width: 500px;
  width: 90%;
}

.modal-content textarea {
  width: 100%;
  height: 100px;
  margin-bottom: var(--goa-space-m);
  padding: var(--goa-space-s);
  border: 1px solid var(--goa-color-border);
  border-radius: var(--goa-border-radius-s);
}

.modal-content button {
  margin-right: var(--goa-space-s);
  padding: var(--goa-space-s) var(--goa-space-m);
  border: 1px solid var(--goa-color-border);
  border-radius: var(--goa-border-radius-s);
  background: var(--goa-color-background);
  cursor: pointer;
}

.modal-content button[type="submit"] {
  background: var(--goa-color-interactive);
  color: white;
  border-color: var(--goa-color-interactive);
}

.modal-content button[type="submit"]:hover {
  background: var(--goa-color-interactive-hover);
}

