import { GoabTab, GoabTabs, GoabText, GoabCallout, GoabButton, GoabButtonGroup } from "@abgov/react-components";
import { useState } from "react";
import { Link } from "react-router-dom";

export default function ResultPage() {
  const [activeTab, setActiveTab] = useState(1);
  const [feedbackSubmitted, setFeedbackSubmitted] = useState(false);
  const [downloadStarted, setDownloadStarted] = useState(false);

  const submissionDetails = {
    referenceNumber: "APP-2024-001234",
    submissionDate: new Date().toLocaleDateString('en-CA', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    }),
    processingTime: "5-10 business days",
    applicationType: "Permit Application",
    contactEmail: "permits@alberta.ca",
    contactPhone: "310-0000"
  };

  const nextStepsInfo = {
    immediate: [
      "Save or print this confirmation page for your records",
      "Check your email for a confirmation message",
      "Note your reference number for future correspondence"
    ],
    upcoming: [
      "We will review your application within 5-10 business days",
      "You will receive an email notification when processing is complete",
      "If additional information is needed, we will contact you directly"
    ],
    followUp: [
      "Track your application status online using your reference number",
      "Contact us if you don't hear from us within the expected timeframe",
      "Submit additional documentation if requested"
    ]
  };

  const relatedServices = [
    {
      title: "Check Application Status",
      description: "Track the progress of your submitted application",
      link: "/application-status",
      external: false
    },
    {
      title: "Submit Additional Documents",
      description: "Upload supporting documents for your application",
      link: "/document-upload",
      external: false
    },
    {
      title: "Contact Support",
      description: "Get help with your application or ask questions",
      link: "/contact-support",
      external: false
    },
    {
      title: "Related Services",
      description: "Explore other services you might need",
      link: "https://alberta.ca/services",
      external: true
    }
  ];

  const handleTabChange = (tabIndex: number) => {
    setActiveTab(tabIndex);
  };

  const handleDownloadConfirmation = () => {
    setDownloadStarted(true);
    // Simulate PDF download
    setTimeout(() => {
      console.log('PDF download started');
      setDownloadStarted(false);
    }, 1000);
  };

  const handleFeedbackSubmission = () => {
    setFeedbackSubmitted(true);
    console.log('Feedback submission initiated');
  };

  const formatReferenceNumber = (refNum: string) => {
    return refNum.replace(/(.{3})/g, '$1-').slice(0, -1);
  };

  const getEstimatedCompletionDate = () => {
    const today = new Date();
    const completionDate = new Date(today);
    completionDate.setDate(today.getDate() + 10); // Add 10 business days
    return completionDate.toLocaleDateString('en-CA', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="result-page-container">
      <GoabTabs initialTab={activeTab} onChange={handleTabChange}>
        <GoabTab heading="Code examples">
          <div className="code-examples-section">
            <h3>Result Page Implementation</h3>
            <p>Code examples for implementing result pages would be displayed here...</p>
          </div>
        </GoabTab>

        <GoabTab heading="Design guidelines">
          <div className="design-guidelines-section">
            <div className="result-page-overview">
              <img
                alt="result page overview"
                src="/images/patterns/form-pattern_results-page.png"
              />
            </div>

            <section className="confirmation-section">
              <GoabCallout type="success" heading="Application Submitted Successfully">
                <div className="confirmation-details">
                  <div className="reference-info">
                    <h3>Reference Number</h3>
                    <div className="reference-number" aria-label="Reference number">
                      {formatReferenceNumber(submissionDetails.referenceNumber)}
                    </div>
                  </div>

                  <div className="submission-summary">
                    <h3>Submission Summary</h3>
                    <dl className="submission-details">
                      <dt>Application Type:</dt>
                      <dd>{submissionDetails.applicationType}</dd>
                      <dt>Submission Date:</dt>
                      <dd>{submissionDetails.submissionDate}</dd>
                      <dt>Expected Processing Time:</dt>
                      <dd>{submissionDetails.processingTime}</dd>
                      <dt>Estimated Completion:</dt>
                      <dd>{getEstimatedCompletionDate()}</dd>
                    </dl>
                  </div>
                </div>
              </GoabCallout>
            </section>

            <section className="next-steps-section">
              <h2>What happens next</h2>

              <div className="immediate-steps">
                <h3>Immediate Steps</h3>
                <ul>
                  {nextStepsInfo.immediate.map((step, index) => (
                    <li key={index}>
                      <GoabText size="body-m" mt="none" mb="xs">{step}</GoabText>
                    </li>
                  ))}
                </ul>
              </div>

              <div className="upcoming-steps">
                <h3>What We'll Do</h3>
                <ul>
                  {nextStepsInfo.upcoming.map((step, index) => (
                    <li key={index}>
                      <GoabText size="body-m" mt="none" mb="xs">{step}</GoabText>
                    </li>
                  ))}
                </ul>
              </div>

              <div className="follow-up-steps">
                <h3>Future Actions</h3>
                <ul>
                  {nextStepsInfo.followUp.map((step, index) => (
                    <li key={index}>
                      <GoabText size="body-m" mt="none" mb="xs">{step}</GoabText>
                    </li>
                  ))}
                </ul>
              </div>
            </section>

            <section className="actions-section">
              <h2>Available Actions</h2>
              <GoabButtonGroup alignment="start" gap="m">
                <GoabButton
                  type="primary"
                  onClick={handleDownloadConfirmation}
                  disabled={downloadStarted}
                  aria-label="Download confirmation PDF">
                  {downloadStarted ? 'Preparing Download...' : 'Download Confirmation PDF'}
                </GoabButton>
                <GoabButton
                  type="secondary"
                  onClick={handleFeedbackSubmission}
                  disabled={feedbackSubmitted}
                  aria-label="Provide feedback about this service">
                  {feedbackSubmitted ? 'Feedback Submitted' : 'Give Feedback'}
                </GoabButton>
              </GoabButtonGroup>
            </section>

            <section className="related-services-section">
              <h2>Related Services</h2>
              <div className="services-grid">
                {relatedServices.map((service, index) => (
                  <div key={index} className="service-card">
                    <h3>
                      {service.external ? (
                        <a href={service.link} target="_blank" rel="noopener noreferrer">
                          {service.title}
                        </a>
                      ) : (
                        <Link to={service.link}>{service.title}</Link>
                      )}
                    </h3>
                    <GoabText size="body-m" mt="s" mb="none">
                      {service.description}
                    </GoabText>
                  </div>
                ))}
              </div>
            </section>

            <section className="contact-section">
              <h2>Contact Information</h2>
              <div className="contact-details">
                <div className="contact-method">
                  <h3>Email</h3>
                  <GoabText size="body-m" mt="s" mb="none">
                    <a href={`mailto:${submissionDetails.contactEmail}`}>
                      {submissionDetails.contactEmail}
                    </a>
                  </GoabText>
                </div>
                <div className="contact-method">
                  <h3>Phone</h3>
                  <GoabText size="body-m" mt="s" mb="none">
                    <a href={`tel:${submissionDetails.contactPhone}`}>
                      {submissionDetails.contactPhone}
                    </a>
                  </GoabText>
                </div>
              </div>
            </section>
          </div>
        </GoabTab>
      </GoabTabs>
    </div>
  );
}

