}

.actions-section {
  margin-bottom: var(--goa-space-2xl);
}

.contact-section {
  margin-bottom: var(--goa-space-xl);
}

.contact-details {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: var(--goa-space-l);
}

@media (max-width: 768px) {
  .contact-details {
